public String mixString(String a, String b) {
  String sb= "";
  if(a.length()==0) return b;
  for(int i=0; i<a.length(); i++){
    sb+=a.substring(i,i+1);
    if(i<b.length()){
      sb+=b.substring(i,i+1);
    }
    if(i==a.length()-1&&b.length()>a.length()){
      sb+=b.substring(i+1);
    }
  }
  return sb;
}

