public boolean evenlySpaced(int a, int b, int c) {

  int temp=0;
  int[] arr={a,b,c};
  int n = arr.length;  

  for(int i=0; i < n; i++){  
      for(int j=1; j < (n-i); j++)
      {  
          if(arr[j-1] > arr[j])
          {  
               //swap elements  
                temp = arr[j-1];  
                arr[j-1] = arr[j];  
               arr[j] = temp;  
          }  

     }  

}
 if(arr[1]-arr[0]==arr[2]-arr[1]) return true;
 return false;
}

