public String wordEnds(String str, String word) {
	String result = "";
	for(int i=0; i<str.length() - word.length(); i++){
		String ss = str.substring(i, i+2);
		if(i == 0){
			if(ss.equals(word)){
				result += str.substring(i+2, i+3);
			}
		}
		if(i == str.length()-word.length()){
			if(ss.equals(word)){
				result += str.substring(i+2, i+3);
			}
		} else {
			if(ss.equals(word)){
				result += str.substring(i-1, i) + str.substring(i+2, i+3);
			}
		}
	}
	return result;
}

public int countYZ(String str) {
  int count =0;

  for(int i=0; i< str.length() -1 ; i++){
  	if(checkMatch(str.charAt(i)) && checkMatch(str.charAt(i)) && !Character.isLetter(str.charAt(i+1))){
    	count++;
  	}
  }
  if(checkMatch(str.charAt(str.length()-1)) || checkMatch(str.charAt(str.length()-1))){
    count++;
  }
  return count;
}

public boolean checkMatch(char character){
	 return Character.toLowerCase(character) == 'y' || Character.toLowerCase(character) == 'z';
}

public String plusOut(String str, String word) {
    StringBuilder newString = new StringBuilder(str.length());
    String[] split = str.split(word);

    for (int i = 0; i < split.length; i++) {
        for (int j = 0; j < split[i].length(); j++)
            newString.append('+');

        if (i != split.length - 1)
            newString.append(word);
    }

    return newString.toString();
}

