public boolean gHappy(String str) {
  if(str.length() < 1) return true;
  if(str.length() < 2 ) return false;
  for(int i=0; i<str.length()-1; i++){
  	if(str.charAt(i) == 'g'){
  		if(!(str.charAt(i+1) == 'g' || str.charAt(i-1) == 'g')){
  			return false;
  		}
  	}
  }
  if(str.charAt(str.length()-1) == 'g' && str.charAt(str.length()-2) != 'g') return false;
  return true;
}

public String mirrorEnds(String string) {
  if(string.length() < 2) return string;
	int count = 0;
  for(int i=0;  i<string.length()/2 +1 ; i++){
    if(string.charAt(i) == string.charAt(string.length()-1-count)){
      count++;
    }
    else break;
  }
  if(count > string.length()/2) return string;
  return string.substring(0, count);
}

public int maxBlock(String str) {
	if(str.length() < 1) return 0;
  int maxBlockLength = 1;
  int currentStreak = 1;
  for(int i=0; i<str.length()-1; i++){
  	if(str.charAt(i) == str.charAt(i+1)){
  		currentStreak++;
  	} else {
  		currentStreak = 1;
  	}
  	if(currentStreak > maxBlockLength) maxBlockLength = currentStreak;
  }
  return maxBlockLength;
}

public int sumNumbers(String str) {
  int result = 0;
  String buffer = "0";
  for(int i=0; i<str.length(); i++){
  	char ch = str.charAt(i);
  	if(Character.isDigit(ch)){
  			buffer +=  ch;
  	} else {
  			result += Integer.parseInt(buffer);
  			buffer = "0";
  	}
  }
  result += Integer.parseInt(buffer);
  return result;
}

