public boolean canBalance(int[] nums) {
	int leftSum = 0;
  int rightSum = 0;
  for(int i=1; i<nums.length; i++){
  	for(int j=0; j<i; j++){
  		leftSum += nums[j];
  	}
  	for(int h=i; h< nums.length; h++){
  		rightSum += nums[h];
  	}
  	if(leftSum == rightSum) return true;
  	leftSum = 0;
  	rightSum = 0;
  }
  return false;
}

