    if(map.containsKey(s)) map.put(s, map.get(s) + 1);
    else map.put(s, 1);
  }
  return map;
}

public Map<String, String> firstChar(String[] strings) {
	Map<String, String> map = new HashMap();
	for(String s:strings){
		map.put(s.substring(0, 1), map.get(s.substring(0, 1)) != null ?  (map.get(s.substring(0, 1)) +s) : s);
	}
	return map;
}

public String wordAppend(String[] strings) {
  Map<String, Integer> map = new HashMap<String, Integer>();
  String result="";
  for(String s:strings){
  	if(!map.containsKey(s)){
  		map.put(s, 1);
  	}
  	else{
  		int count = map.get(s);
  		map.put(s, count + 1);
  		count = map.get(s);
  		if(count !=1 && count %2 == 0){
  			result += s;
  		}
  	}
  }
  return result;
}

public Map<String, Boolean> wordMultiple(String[] strings) {
  Map<String, Boolean> map = new HashMap();
  for(String s:strings){
  	if(!map.containsKey(s)){
  		map.put(s, false);
  	}
  	else{
  		map.put(s, true);
  	}
  }
  return map;
}

public Map<String, String> mapAB4(Map<String, String> map) {
  if(map.containsKey("a") && map.containsKey("b")){
    if(map.get("a").length() == map.get("b").length()){
      map.put("a", "");
      map.put("b", "");
    } else {
      map.put("c", map.get("a").length() > map.get("b").length() ? map.get("a"): map.get("b"));
    }
  }
  return map;
}

public Map<String, String> mapAB2(Map<String, String> map) {
  if(map.containsKey("a") && map.containsKey("b") && map.get("a").equals(map.get("b"))){
    map.remove("a");
    map.remove("b");
  }
  return map;
}

public Map<String, String> pairs(String[] strings) {
  Map <String, String>  map = new HashMap();
  for(String s:strings){
    map.put(s.substring(0, 1), s.substring(s.length()-1));
  }
  return map;
}

public Map<String, String> mapAB3(Map<String, String> map) {
  if(map.containsKey("a") && !map.containsKey("b")){
    map.put("b", map.get("a"));
  }
  if(map.containsKey("b") && !map.containsKey("a")){
    map.put("a", map.get("b"));
  }
  return map;
}

public String[] allSwap(String[] strings) {
  Map<Character, Integer> map = new HashMap();
  for(int i=0; i< strings.length; i++){
  	Character c = strings[i].charAt(0);
  	if(!map.containsKey(c)){
  		map.put(c, i);
  	}
  	else{
  		String temp = strings[i];
  		strings[i] = strings[map.get(c)];
  		strings[map.get(c)] = temp;
  		map.remove(c);
  	}
  }
  return strings;
}

public String[] firstSwap(String[] strings) {
  Map<Character, Integer> map = new HashMap();
  for(int i=0; i< strings.length; i++){
  	Character c = strings[i].charAt(0);
  	if(!map.containsKey(c)){
  		map.put(c, i);
  	}
  	else{
  		if(map.get(c) > -1){
  			String temp = strings[i];
	  		strings[i] = strings[map.get(c)];
	  		strings[map.get(c)] = temp;
	  		map.put(c, -1);
  		}
  	}
  }
  return strings;
}

